import 'package:flutter/material.dart';class VerificationCode extends StatefulWidget {  const VerificationCode({super.key});  @override  _VerificationCodeState createState() => _VerificationCodeState();}class _VerificationCodeState extends State<VerificationCode> {  final _formKey = GlobalKey<FormState>();  String? _otp;  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: const Text('Verification Code'),      ),      body: Padding(        padding: const EdgeInsets.all(16.0),        child: Column(          children: [            const SizedBox(height: 16.0),            const Text(              'Verification Code',              style: TextStyle(                fontWeight: FontWeight.bold,                fontSize: 24.0,              ),            ),            const SizedBox(height: 16.0),            const Text(              'We have sent the verification code to your email address',              textAlign: TextAlign.center,            ),            const SizedBox(height: 16.0),            Form(              key: _formKey,              child: TextFormField(                decoration: const InputDecoration(                  labelText: 'OTP',                ),                maxLength: 4,                keyboardType: TextInputType.number,                textAlign: TextAlign.center,                style: const TextStyle(                  fontSize: 24.0,                  fontWeight: FontWeight.bold,                ),                validator: (value) {                  if (value!.isEmpty) {                    return 'Please enter the OTP';                  }                  return null;                },                onSaved: (value) {                  _otp = value;                },              ),            ),            const SizedBox(height: 16.0),            ElevatedButton(              onPressed: () {                if (_formKey.currentState!.validate()) {                  _formKey.currentState!.save();                  // TODO: Verify OTP                  Navigator.pushReplacementNamed(context, 'home');                }              },              child: const Text('Verify OTP'),            ),          ],        ),      ),    );  }}